mapper.insert:
insert
  into qna
  (
  	   qa_no
  	 , mem_no
  	 , lesson_no
  	 , question
  	 , q_enroll_date
  )
values
  (
       seq_qnano.nextval
     , #{qnaNo}
     , #{memNo}
     , #{lessonNo}
     , #{question}
     , sysdate
  )

mapper.update:
update qna
   set question = #{question}
     , q_enroll_date = sysdate
 where qa_no = #{qnaNo}

update qna
   set q_status = 'N'
 where qa_no = #{qnaNo}

update qna
   set answer = #{answer}
     , a_enroll_date = sysdate
     , a_status = 'Y'
 where qa_no = #{qnaNo}

update qna
   set answer = #{answer}
     , a_enroll_date = sysdate
 where qa_no = #{qnaNo}

update qna
   set a_status = 'N'
 where qa_no = #{qnaNo}

update mem_video
   set status = 'Y'
 where video_no = #{videoNo}
   and mem_no = #{memNo}

