mapper.select:
select *
  from lesson_faq
 where lesson_no = #{lessonNo}

mapper.update:
	update 
       reply
   set 
       reply_content = #{replyContent}
     , modify_date = SYSDATE
 where reply_no = #{replyNo}
   and reply_no2 = #{replyNo2}
   and reply_type = '클래스'

mapper.insert:
insert 
  into report
     (
       mem_no
     , reply_no
     , report_type
     )
 values
     (
        #{memNo}
      , #{replyNo}
      , #{reportType}
     )

insert 
  into lesson_faq
   (
      lesson_no
    , faq_question
    , faq_answer
   )
   values
   (
     #{lessonNo}
   , #{faqQuestion}
   , #{faqAnswer}
   )

insert 
  into mem_pay
    (
      mem_pay_no
    , mem_no
    , lesson_no
    , type
    , del_postcode
    , del_post_adr
    , del_detail_adr
    , del_extra_adr
    )
values
    (
      seq_mpay_no.nextval
    , #{memNo}
    , #{lessonNo}
    , #{type}
    , #{delPostcode}
    , #{delPostAdr}
    , #{delDetailAdr}
    , #{delExtraAdr}
    )
