mapper.insert:
insert
  into notice
  (
     notice_no
   , notice_type
   , notice_title
   , notice_content
   , enroll_date
   , status
  )
  values
  (
     notice_no.nextval
   , #{noticeType}
   , #{noticeTitle}
   , #{noticeContent}
   , #{enrollDate}
   , #{status}
  )

mapper.update:
update 
       notice
   set notice_type = #{noticeType}
     , notice_title = #{noticeTitle}
     , notice_content = #{noticeContent}
     , enroll_date = #{enrollDate}
     , status = #{status}
 where notice_no = #{noticeNo}
