mapper.insert:
insert
  into review
    (
       review_no
     , lesson_no
     , mem_no
     , star
     , review_content
    )
    values
    (
      seq_rev_no.nextval
    , #{lessonNo}
    , #{memNo}
    , #{star}
    , #{reviewContent}
    )

mapper.update:
update
        review
   set
        review_content = #{reviewContent}
      , modify_date = SYSDATE
      , star = #{star}
 where review_no = #{reviewNo}
