mapper.insert:
insert
  into tutor
  (
  	mem_no
  , tutor_pic
  , tutor_info
  )
  values
  (
  	#{memNo}
  , #{tutorPic}
  , #{tutorInfo}
  )

mapper.update:
update
		tutor
	set tutor_pic = #{tutorPic}
	  , tutor_info = #{tutorInfo}
	  , modify_date = TO_CHAR(SYSDATE,'YY/MM/DD')
  where mem_no = #{memNo}
